return items.map(item => {
  const email = item.json.Email || ""; // fallback if missing
  let domain = null;
  let verified = false;

  if (email.includes("@")) {
    domain = email.split('@')[1];
    verified = domain === "example.com";
  }

  return {
    json: {
      ...item.json,
      domain,
      verified
    }
  };
});
